B[2] = intersectionpoints(graph(f,-atan(3)/2+2*pi/2,atan(3)/2+2*pi/2),graph(g,0,2pi))[0];
B[3] = intersectionpoints(graph(f,-atan(3)/2+3*pi/2,atan(3)/2+3*pi/2),graph(g,0,2pi))[0];
