draw((0,1)--(0.5,1)--(0.5,1.5)--(0,1.5)--(0,1));
draw((0.5,0.5)--(1,0.5)--(1,1)--(0.5,1)--(0.5,0.5));
draw((1,0)--(1.5,0)--(1.5,0.5)--(1,0.5)--(1,0));
