real d=11/102;
defaultpen(fontsize(8));
defaultpen(linewidth(0.8));
currentprojection=orthographic(1,8/15,7/15);
draw(unitcube, white, thick(), nolight);
void f(real x) {
draw((0,1,x)--(1,1,x)--(1,0,x));
}
f(d);
f(1/6);
f(1/2);
