<asy>
// Asymptote by aoum
unitsize(5mm);

path createpath(pair[][] P, int[] p) {
  int i=0;
  int j=0;
  path dp = P[0][0];
  for (int s=0; s<10; ++s) {
    if (p[s] == 0) {++i;} else {++j;}
    dp = dp--P[i][j];
  }
  return(dp);
}

