for (int i=0; i<6; ++i) {
  A[i] = new pair[];
  B[i] = new pair[];
  for (int j=0; j<6; ++j) {
    A[i].push(rotate(45)*(i,j));
    B[i].push(shift(9,0)*A[i][j]);
  }
}

